SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_lote_acm_sn (nr_seq_lote_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


qt_existe_w	bigint;
ds_retorno_w	varchar(01);



BEGIN

select	count(*)
into STRICT	qt_existe_w
from   	prescr_mat_hor  b,
	prescr_material c,
	prescr_medica   a
where	a.nr_prescricao = b.nr_prescricao
and	a.nr_prescricao	= c.nr_prescricao
and	c.nr_sequencia	= b.nr_seq_material
and 	b.nr_seq_lote   = nr_seq_lote_p
and	((coalesce(c.ie_acm,'N') = 'S' and ie_opcao_p = 'ACM')
	or (coalesce(c.ie_se_necessario,'N') = 'S' and ie_opcao_p = 'SN'));

if (coalesce(qt_existe_w,0) > 0) then
	ds_retorno_w := 'S';
else
	ds_retorno_w := 'N';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
