CREATE OR REPLACE FUNCTION obter_se_lote_contabil_gerado ( cd_tipo_lote_contabil_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter se o lote contabil ja foi gerado no mes de referencia
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atencao:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w		varchar(1);
dt_referencia_month_w	timestamp;
qt_lote_gerado_mes_w	bigint;
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;


BEGIN
ds_retorno_w		:= 'N';
dt_referencia_month_w	:= ESTABLISHMENT_TIMEZONE_UTILS.startOfmonth(dt_referencia_p);
cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;

select	count(1)
into STRICT	qt_lote_gerado_mes_w
from	lote_contabil
where	cd_tipo_lote_contabil	= cd_tipo_lote_contabil_p
and	ESTABLISHMENT_TIMEZONE_UTILS.startOfmonth(dt_referencia) = dt_referencia_month_w
and	(dt_geracao_lote IS NOT NULL AND dt_geracao_lote::text <> '')
and	coalesce(ie_status_origem, 'M') <> 'SO'
and	cd_estabelecimento 		= cd_estabelecimento_w;

if (qt_lote_gerado_mes_w > 0) then
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
