SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_lote_entrada_sem_acao ( nr_seq_ficha_p bigint) RETURNS varchar AS $body$
DECLARE


qt_alert_w			bigint;
nr_seq_inst_w		bigint;
nr_seq_lote_w		bigint;
ie_sem_acao_w		varchar(1) := 'N';
qt_reg_reconv_w		bigint;


BEGIN

if (nr_seq_ficha_p IS NOT NULL AND nr_seq_ficha_p::text <> '') then

	select	count(*)
	into STRICT	qt_reg_reconv_w
	from	lote_ent_reconvocado a, lote_ent_reconvocado_cont b
	where	a.nr_sequencia = b.nr_seq_reconvocado
	and		a.nr_seq_ficha_lote = nr_seq_ficha_p;

	if (qt_reg_reconv_w > 0) then
		select	max(nr_seq_lote_sec)
		into STRICT	nr_seq_lote_w
		from	lote_ent_sec_ficha
		where	nr_sequencia = nr_seq_ficha_p;

		select	max(nr_seq_instituicao)
		into STRICT	nr_seq_inst_w
		from	lote_ent_secretaria
		where	nr_sequencia = nr_seq_lote_w;

		select 	max(qt_horas)
		into STRICT	qt_alert_w
		from 	LOTE_ENT_INST_ALERT
		where	nr_seq_instituicao = nr_seq_inst_w;

		if (qt_alert_w > 0) then

			select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
			into STRICT	ie_sem_acao_w
			from	lote_ent_reconvocado a, lote_ent_reconvocado_cont b
			where	a.nr_sequencia = b.nr_seq_reconvocado
			and		coalesce(a.dt_suspensao_rec::text, '') = ''
			and 	a.nr_seq_ficha_lote = nr_seq_ficha_p
			and		b.dt_atualizacao > clock_timestamp() - qt_alert_w/24;

		end if;

	end if;

end if;

return	ie_sem_acao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_lote_entrada_sem_acao ( nr_seq_ficha_p bigint) FROM PUBLIC;

