CREATE OR REPLACE FUNCTION obter_se_lote_forn_restricao ( cd_material_p bigint, ds_lote_fornec_p text) RETURNS varchar AS $body$
DECLARE


ds_lote_fornec_w	lote_fornec_restricao.ds_lote_fornec%type;
ie_bloqueio_w	varchar(1);


BEGIN
ds_lote_fornec_w	:=	upper(substr(ds_lote_fornec_p,1,20));

begin
select	'S'
into STRICT	ie_bloqueio_w
from	lote_fornec_restricao
where	cd_material = cd_material_p
and	upper(ds_lote_fornec) = ds_lote_fornec_w
and	ie_bloquear_lote_nf = 'S'
and	ie_situacao = 'A'  LIMIT 1;
exception
when others then
	ie_bloqueio_w	:=	'N';
end;

return	ie_bloqueio_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
