CREATE OR REPLACE FUNCTION obter_se_lote_hor_conta_pac (nr_seq_horario_p bigint) RETURNS varchar AS $body$
DECLARE


ie_conta_paciente_w	varchar(1) := 'S';


BEGIN
if (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') then

	select	coalesce(max(b.ie_conta_paciente),'S')
	into STRICT	ie_conta_paciente_w
	from	ap_lote b,
		prescr_mat_hor a
	where	b.nr_sequencia = a.nr_seq_lote
	and	a.nr_sequencia = nr_seq_horario_p
	and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

end if;

return ie_conta_paciente_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
