CREATE OR REPLACE FUNCTION obter_se_lote_prot_contabil ( nr_seq_lote_protocolo_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_contabilizado_w		varchar(1);


BEGIN 
 
select	CASE WHEN coalesce(max(nr_lote_contabil),0)=0 THEN 'N'  ELSE 'S' END  
into STRICT	ie_contabilizado_w 
from	conta_paciente a, 
	protocolo_convenio b 
where	a.nr_seq_protocolo = b.nr_seq_protocolo 
and	b.nr_seq_lote_protocolo = nr_seq_lote_protocolo_p;
 
return	ie_contabilizado_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
