CREATE OR REPLACE FUNCTION obter_se_lote_rec_integrado ( cd_convenio_p bigint, ie_tipo_protocolo_p bigint, dt_mesano_referencia_p timestamp, cd_estabelecimento_p bigint default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w    varchar(1) := 'N';


BEGIN

select  CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
into STRICT    ds_retorno_w
from    conta_paciente b,
        protocolo_convenio a
where   b.nr_seq_protocolo      = a.nr_seq_protocolo
and     a.cd_convenio           = cd_convenio_p
and     a.ie_tipo_protocolo     = ie_tipo_protocolo_p
and     a.dt_mesano_referencia   between trunc(dt_mesano_referencia_p,'month') and last_day(dt_mesano_referencia_p)
and     (nr_lote_contabil IS NOT NULL AND nr_lote_contabil::text <> '')
and     b.cd_estabelecimento = a.cd_estabelecimento
and (b.cd_estabelecimento = cd_estabelecimento_p or coalesce(cd_estabelecimento_p::text, '') = '');

return  ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
