CREATE OR REPLACE FUNCTION obter_se_lote_rotina ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_horario_atend_w	timestamp;
ds_horario_ger_w	timestamp;
ds_retorno_w		varchar(1);
nr_seq_classif_w	smallint;


BEGIN

select 	w.dt_atend_lote,
	w.dt_geracao_lote,
	w.nr_seq_classif
into STRICT	ds_horario_atend_w,
	ds_horario_ger_w,
	nr_seq_classif_w
from	ap_lote w
where	w.nr_sequencia = nr_sequencia_p;

if (ds_horario_ger_w > ds_horario_atend_w) then
	ds_retorno_w := 'S';
else
	ds_retorno_w := 'N';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
