CREATE OR REPLACE FUNCTION obter_se_lote_unitarizado (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_unitarizado_w varchar(1);

BEGIN
select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_unitarizado_w
from	solic_unitarizacao
where	nr_seq_lote_fornec = nr_sequencia_p
and	(dt_envio IS NOT NULL AND dt_envio::text <> '');

return	ie_unitarizado_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
