CREATE OR REPLACE FUNCTION obter_se_lote_valido (dt_inicial_p timestamp, dt_final_p timestamp, nr_sequencia_p bigint default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
qt_registro_W	bigint;


BEGIN


select	count(*)
into STRICT	qt_registro_w
from 	fis_livro_fiscal
where	to_date(dt_inicial_p,'dd/mm/yy') between dt_inicial and dt_final
and	to_date(dt_final_p,'dd/mm/yy') between dt_inicial and dt_final
and	nr_sequencia = nr_sequencia_p;



if (qt_registro_W > 0) then
	ds_retorno_w	:= 'S';
else
	ds_retorno_w	:= 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
