CREATE OR REPLACE FUNCTION obter_se_macro_liberada ( nr_seq_macro_p bigint, cd_pais_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);


BEGIN

select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
into STRICT	ds_retorno_w
from	funcao_macro_pais
where	nr_seq_macro = nr_seq_macro_p;

if (ds_retorno_w = 'N') then
	select	CASE WHEN count(1)=1 THEN 'S'  ELSE 'N' END
	into STRICT	ds_retorno_w
	from	funcao_macro_pais
	where	nr_seq_macro = nr_seq_macro_p
	and 	cd_pais = cd_pais_p;
end if;


return	ds_retorno_w;

end;
$body$
