CREATE OR REPLACE FUNCTION obter_se_maior_limite_sus (nr_Atendimento_p bigint, qt_dias_perm_sus_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1)	:= 'N';
qt_dias_internacao_w	bigint;


BEGIN

select	Obter_Dias_Internacao(nr_atendimento_p)
into STRICT	qt_dias_internacao_w
;

if (qt_dias_internacao_w > qt_dias_perm_sus_p) then
	ds_Retorno_w	:= 'S';
elsif (qt_dias_internacao_w = qt_dias_perm_sus_p) then
	ds_retorno_w	:= 'I';
end if;

return	ds_retorno_w;

end;
$body$
