SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_marca_hor_esp ( nr_atendimento_p bigint, nr_prescricao_p bigint, ie_parametro_p text) RETURNS varchar AS $body$
DECLARE

ie_retorno_w	varchar(1) := 'N';
qt_reg_w		bigint;
dt_fim_w	timestamp;


BEGIN

if (ie_parametro_p = 'P') or (ie_parametro_p = 'T') then

	select	count(nr_prescricao)
	into STRICT	qt_reg_w
	from	prescr_medica
	where	nr_atendimento = nr_atendimento_p
	and	nr_prescricao <> nr_prescricao_p;

	if (qt_reg_w > 0) then
		ie_retorno_w := 'N';
	else
		ie_retorno_w := 'S';
	end if;
end if;

if (ie_parametro_p = 'J') or
	(ie_parametro_p = 'T' AND ie_retorno_w = 'N')	then

	select	max(c.dt_fim)
	into STRICT	dt_fim_w
	from	rep_jejum c,
		prescr_medica d
	where	d.nr_prescricao = c.nr_prescricao
	and 	d.nr_atendimento = nr_atendimento_p;

	if (dt_fim_w IS NOT NULL AND dt_fim_w::text <> '') then

		select	count(a.nr_prescricao)
		into STRICT	qt_reg_w
		from	prescr_medica a,
			prescr_dieta_hor b
		where	a.nr_prescricao = b.nr_Prescricao
		and	a.nr_atendimento = nr_atendimento_p
		and	b.nr_prescricao <> nr_prescricao_p
		and	b.dt_horario > dt_fim_w;

		if (qt_reg_w > 0) then
			ie_retorno_w := 'N';
		else
			ie_retorno_w := 'S';
		end if;
	else
		ie_retorno_w := 'N';
	end if;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
