CREATE OR REPLACE FUNCTION obter_se_material_baixa_prescr ( cd_estabelecimento_p bigint, cd_local_estoque_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


qt_existe_w			smallint;
ie_baixa_estoque_w		varchar(1);
vl_retorno_w			varchar(1);
cd_grupo_material_w		smallint;
cd_subgrupo_w			smallint;
cd_classe_material_w		integer;
ie_receita_w			varchar(1);


BEGIN
ie_baixa_estoque_w	:= 'N';

select	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_w,
	cd_classe_material_w
from	estrutura_material_v
where	cd_material	= cd_material_p;

select 	coalesce(max(ie_receita),'N')
into STRICT	ie_receita_w
from 	material
where 	cd_material = cd_material_p;

select	count(*)
into STRICT	qt_existe_w
from	baixa_material_prescr
where	cd_estabelecimento			= cd_estabelecimento_p
and	cd_local_estoque			= cd_local_estoque_p
and	coalesce(cd_grupo_mat, cd_grupo_material_w)	= cd_grupo_material_w
and	coalesce(cd_subgrupo_mat, cd_subgrupo_w)	= cd_subgrupo_w
and	coalesce(cd_classe_mat, cd_classe_material_w)= cd_classe_material_w
and 	((coalesce(ie_receita,'A') = 'A') or (coalesce(ie_receita,'A') = ie_receita_w))
and	coalesce(cd_material, cd_material_p)		= cd_material_p;
if (qt_existe_w > 0) then
	ie_baixa_estoque_w	:= 'S';
end if;

return ie_baixa_estoque_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
