CREATE OR REPLACE FUNCTION obter_se_material_dupl ( nr_atendimento_p bigint, nr_prescricao_p bigint, cd_material_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp) RETURNS varchar AS $body$
DECLARE

ie_duplicado_w			varchar(1) := 'N';
dt_inicio_prescr_w		timestamp;
dt_validade_prescr_w	timestamp;

BEGIN

select	max(dt_inicio_prescr),
		max(dt_validade_prescr)
into STRICT		dt_inicio_prescr_w,
		dt_validade_prescr_w
from		prescr_medica
where	nr_prescricao = nr_prescricao_p;

select	coalesce(max('S'),'N')
into STRICT		ie_duplicado_w
from		prescr_material		a,
		prescr_medica		b
where	b.nr_prescricao			<> nr_prescricao_p
and		b.nr_atendimento 		= nr_atendimento_p
and		a.cd_material			= cd_material_p
and		b.dt_inicio_prescr between dt_inicio_prescr_w and dt_validade_prescr_w
and		b.dt_validade_prescr between dt_inicio_prescr_w and dt_validade_prescr_w
and		a.nr_prescricao			= b.nr_prescricao
and		(coalesce(b.dt_liberacao,b.dt_liberacao_medico) IS NOT NULL AND (coalesce(b.dt_liberacao,b.dt_liberacao_medico))::text <> '')
and		coalesce(a.ie_modificado,'N')	<> 'S'
and		a.ie_agrupador = 1
and		coalesce(a.ie_suspenso,'N') 	= 'N';

return ie_duplicado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
