CREATE OR REPLACE FUNCTION obter_se_material_estrut_loc ( cd_local_estoque_p bigint, cd_material_p bigint, nr_seq_estrut_mat_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'N';


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_retorno_w
from (
	SELECT	nr_seq_estrut_mat
	from	localizacao_estoque_local a
	where	a.cd_material 	= cd_material_p
	and	a.cd_local_estoque = cd_local_estoque_p
	and	(a.nr_seq_estrut_mat IS NOT NULL AND a.nr_seq_estrut_mat::text <> '')
	and	exists (WITH RECURSIVE cte AS (

			SELECT	/*+ result_cache */ x.nr_sequencia
			from	mat_estrutura x WHERE x.nr_sequencia = nr_seq_estrut_mat_p
  UNION ALL

			SELECT	
 x.nr_sequencia
			from	mat_estrutura x JOIN cte c ON (c.prior nr_sequencia = x.x.nr_seq_estrut_sup)

) SELECT * FROM cte WHERE nr_sequencia = nr_seq_estrut_mat;
)
	
union all

	select	a.nr_seq_estrut_mat
	from	padrao_estoque_local a
	where	a.cd_material	= cd_material_p
	and	a.cd_local_estoque	= cd_local_estoque_p
	and	(a.nr_seq_estrut_mat IS NOT NULL AND a.nr_seq_estrut_mat::text <> '')
	and	not exists (	select	1
				from	localizacao_estoque_local x
				where	x.cd_material = a.cd_material
				and	x.cd_local_estoque = a.cd_local_estoque)
	and	exists (WITH RECURSIVE cte AS (
(
			select	/*+ result_cache */ x.nr_sequencia
			from	mat_estrutura x WHERE x.nr_sequencia = nr_seq_estrut_mat_p
  UNION ALL
(
			select	
 x.nr_sequencia
			from	mat_estrutura x JOIN cte c ON (c.prior nr_sequencia = x.x.nr_seq_estrut_sup
			))

) SELECT * FROM cte WHERE nr_sequencia = nr_seq_estrut_mat;
) ) LIMIT 1 alias7;

return	ie_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
