CREATE OR REPLACE FUNCTION obter_se_material_exige_justif (cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_exige_justif_w	varchar(1);


BEGIN
if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

	select	coalesce(obter_dados_medic_atb_var(max(cd_material),wheb_usuario_pck.get_cd_estabelecimento,max(ie_obriga_justificativa),'OJ',null,null,null),'N')
	into STRICT		ie_exige_justif_w
	from		material
	where	cd_material = cd_material_p;

end if;

return ie_exige_justif_w;

end;
$body$
