CREATE OR REPLACE FUNCTION obter_se_material_inv_periodo ( cd_material_p bigint, cd_estabelecimento_p bigint, cd_local_estoque_p bigint, qt_dias_inventario_p bigint) RETURNS varchar AS $body$
DECLARE


cd_material_estoque_w	integer;
qt_inventario_w		bigint;
ds_retorno_w		varchar(1);
dt_inicio_consulta_w	timestamp;


BEGIN
begin
select	cd_material_estoque
into STRICT	cd_material_estoque_w
from	material
where	cd_material = cd_material_p;
exception
when others then
	cd_material_estoque_w	:=	cd_material_p;
end;

dt_inicio_consulta_w	:=	(trunc(clock_timestamp() - qt_dias_inventario_p,'dd') - 1/86400);

begin

select	1
into STRICT	qt_inventario_w
from	inventario a,
	inventario_material b
where	a.nr_sequencia = b.nr_seq_inventario
and	a.cd_estabelecimento = cd_estabelecimento_p
and	a.cd_local_estoque = cd_local_estoque_p
and	cd_material = cd_material_estoque_w
and	(a.dt_atualizacao_saldo IS NOT NULL AND a.dt_atualizacao_saldo::text <> '')
and	(b.qt_inventario IS NOT NULL AND b.qt_inventario::text <> '')
and	a.dt_bloqueio > dt_inicio_consulta_w  LIMIT 1;
exception
when others then
	begin
	select	1
	into STRICT	qt_inventario_w
	from	movimento_estoque
	where	cd_estabelecimento = cd_estabelecimento_p
	and	cd_local_estoque = cd_local_estoque_p
	and	cd_material_estoque = cd_material_estoque_w
	and	dt_movimento_estoque > dt_inicio_consulta_w
	and	ie_origem_documento = '5'
	and	(dt_processo IS NOT NULL AND dt_processo::text <> '')
	and	cd_acao = 1  LIMIT 1;
	exception
	when others then
		qt_inventario_w	:=	0;
	end;
end;

if (qt_inventario_w > 0) then
	ds_retorno_w := 'S';
else
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
