CREATE OR REPLACE FUNCTION obter_se_material_lib_setor (cd_estabelecimento_p bigint, cd_setor_atendimento_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


cd_local_estoque_w	smallint;
ie_local_valido_w varchar(1);
ie_liberado_ww	varchar(1);

C01 CURSOR FOR
	SELECT	cd_local_estoque
	from	setor_atendimento
	where 	cd_setor_atendimento = cd_setor_atendimento_p
	group by cd_local_estoque
	
union

	SELECT	cd_local_estoque
	from	setor_local
	where	cd_setor_atendimento = cd_setor_atendimento_p
	group by cd_local_estoque;

BEGIN
ie_liberado_ww := 'N';
open C01;
loop
fetch C01 into
	cd_local_estoque_w;
exit when(C01%notfound or ie_liberado_ww = 'S');
	begin
	ie_local_valido_w := OBTER_LOCAL_VALIDO(
		cd_estabelecimento_p, cd_local_estoque_w, cd_material_p, null, ie_local_valido_w);

	if (ie_local_valido_w = 'S') then
		ie_liberado_ww := 'S';
	end if;
	end;
end loop;
close C01;

return	ie_liberado_ww;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
