SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_material_operacao ( cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1)	:= '';
cd_classe_material_w		regra_oper_terc_material.cd_classe_material%type;
cd_grupo_material_w		regra_oper_terc_material.cd_grupo_material%type;
cd_subgrupo_material_w		regra_oper_terc_material.cd_subgrupo_material%type;
cd_material_w			regra_oper_terc_material.cd_material%type;


BEGIN

select	coalesce(max('N'),'S')
into STRICT	ds_retorno_w
from	regra_operacao_terceiro a
where	clock_timestamp() between a.dt_inicio_vigencia and a.dt_fim_vigencia;

if (ds_retorno_w = 'N') then

	select	a.cd_material,
		a.cd_classe_material,
		a.cd_subgrupo_material,
		a.cd_grupo_material
	into STRICT	cd_material_w,
		cd_classe_material_w,
		cd_subgrupo_material_w,
		cd_grupo_material_w
	from	estrutura_material_v a
	where	a.cd_material	= cd_material_p;

	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	regra_operacao_terceiro b,
		regra_oper_terc_material a
	where	clock_timestamp()							between b.dt_inicio_vigencia and b.dt_fim_vigencia
	and	a.nr_seq_regra						= b.nr_sequencia
	and	coalesce(a.cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
	and	coalesce(a.cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
	and	coalesce(a.cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
	and	coalesce(a.cd_material, cd_material_w)			= cd_material_w;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
