CREATE OR REPLACE FUNCTION obter_se_material_receita ( nr_seq_home_care_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'N';



BEGIN

if (nr_seq_home_care_p IS NOT NULL AND nr_seq_home_care_p::text <> '') then

	select coalesce(max('S'),'N')
	into STRICT ds_retorno_w
	from hc_paciente_material a,
		fa_medic_farmacia_amb b
	where a.cd_material = b.cd_material
	and a.nr_seq_pac_home_care = nr_seq_home_care_p
	and (a.cd_intervalo IS NOT NULL AND a.cd_intervalo::text <> '')
	and (a.cd_unidade_medida IS NOT NULL AND a.cd_unidade_medida::text <> '')
	and (a.qt_material IS NOT NULL AND a.qt_material::text <> '');

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
