CREATE OR REPLACE FUNCTION obter_se_material_risco_prm ( nr_sequencia_p bigint ) RETURNS varchar AS $body$
DECLARE


cd_setor_atend_w 		bigint;
cd_material_w		bigint;
ie_risco_w		varchar(1);
ds_retorno_w		varchar(1) := 'N';


C01 CURSOR FOR
	SELECT	cd_material
	from	prm_ficha_material
	where	nr_seq_ficha = nr_sequencia_p
	order by cd_material;


BEGIN
cd_setor_atend_w  := wheb_usuario_pck.get_cd_setor_atendimento;

open C01;
loop
fetch C01 into
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_risco_w := obter_se_material_risco(cd_setor_atend_w,cd_material_w);
	if (ie_risco_w =  'S') then
		ds_retorno_w := 'S';
	end if;
	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
