CREATE OR REPLACE FUNCTION obter_se_material_risco ( cd_setor_atendimento_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_material_risco_w			varchar(1) := 'N';	
qt_existe_w			bigint;
			

BEGIN

select	count(*)
into STRICT	qt_existe_w
from	material_risco
where	coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_p,0)) = coalesce(cd_setor_atendimento_p,0)
and	cd_material = cd_material_p
and	coalesce(ie_material_risco,'N') = 'S'
and ((coalesce(cd_estabelecimento::text, '') = '') or (cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento));

if (qt_existe_w > 0) then
	ie_material_risco_w := 'S';
end if;

return	ie_material_risco_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
