CREATE OR REPLACE FUNCTION obter_se_material_seg_compras ( cd_empresa_p bigint, cd_estabelecimento_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


cd_grupo_material_w		bigint;
cd_subgrupo_material_w		bigint;
cd_classe_material_w		bigint;
nr_seq_familia_w			bigint;
qt_registro_w			bigint;
ie_possui_segmento_w		varchar(1) := 'N';
ie_curva_abc_w			varchar(1);


BEGIN

select	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material,
	coalesce(nr_seq_familia,0)
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w,
	nr_seq_familia_w
from	estrutura_material_v
where	cd_material = cd_material_p;

ie_curva_abc_w	:= SUBSTR(Obter_Curva_ABC_Estab(cd_estabelecimento_p, cd_material_p, 'N',clock_timestamp()),1,1);

select	count(*)
into STRICT	qt_registro_w
from	segmento_compras a,
	segmento_compras_estrut b
where	a.nr_sequencia	= b.nr_seq_segmento
and	a.ie_situacao 	= 'A'
and	a.cd_empresa	= cd_empresa_p
and	coalesce(a.cd_estabelecimento, cd_estabelecimento_p)		= cd_estabelecimento_p
and	coalesce(b.ie_curva_abc,ie_curva_abc_w) = ie_curva_abc_w
and	coalesce(b.cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
and	coalesce(b.cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(b.cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
and	coalesce(b.nr_seq_familia,coalesce(nr_seq_familia_w,0))		= coalesce(nr_seq_familia_w,0)
and (coalesce(cd_material, cd_material_p) 			= cd_material_p or cd_material_p = 0);

if (qt_registro_w > 0) then
	ie_possui_segmento_w := 'S';
end if;

return	ie_possui_segmento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
