CREATE OR REPLACE FUNCTION obter_se_matetial_int_opme ( nr_atendimento_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


cd_grupo_material_w		smallint;
cd_subgrupo_material_w		smallint;
cd_classe_material_w		integer;
ie_permite_w			varchar(1);
qt_regra_w			integer;
ie_possui_regra_w		integer;
cd_material_w			bigint;




BEGIN
ie_permite_w := 'N';

select 	max(cd_material)
into STRICT	cd_material_w
from  	material_atend_paciente
where 	nr_atendimento = nr_atendimento_p
and 	cd_setor_atendimento = cd_setor_atendimento_p
and   	obter_regra_int_opme_material(cd_material,'3') =  'S';

if (coalesce(cd_material_w,0) > 0 ) then
	begin
	select	coalesce(cd_grupo_material,0),
		coalesce(cd_subgrupo_material,0),
		coalesce(cd_classe_material,0)
	into STRICT	cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w
	from	estrutura_material_v
	where	cd_material	= cd_material_w;

	select	count(*)
	into STRICT	ie_possui_regra_w
	from	regra_int_opme_material
	where (coalesce(cd_grupo_material, cd_grupo_material_w)     	= cd_grupo_material_w or cd_grupo_material_w = 0)
	and (coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w or cd_subgrupo_material_w = 0)
	and (coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w or cd_classe_material_w = 0)
	and (coalesce(cd_material, cd_material_W) 			= cd_material_w or cd_material_w = 0)
	order by
			coalesce(cd_material, 0),
			coalesce(cd_classe_material, 0),
			coalesce(cd_subgrupo_material, 0),
			coalesce(cd_grupo_material, 0);
	end;
end if;

if (coalesce(ie_possui_regra_w,0) > 0) then
	ie_permite_w	:= 'S';
end if;

return ie_permite_w;

end;
$body$
