CREATE OR REPLACE FUNCTION obter_se_matkit_baixa_estoque ( cd_material_p bigint, cd_kit_material_p bigint, nr_seq_item_kit_p bigint default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
cd_estabelecimento_w	smallint;


BEGIN
begin
select	coalesce(ie_baixa_estoque,'S')
into STRICT	ds_retorno_w
from	componente_kit
where	cd_kit_material = cd_kit_material_p
and	nr_sequencia = nr_seq_item_kit_p;
exception
when others then
	begin
	cd_estabelecimento_w	:= 	wheb_usuario_pck.get_cd_estabelecimento;

	select	coalesce(ie_baixa_estoque,'S')
	into STRICT	ds_retorno_w
	from	componente_kit
	where	cd_kit_material		= cd_kit_material_p
	and (cd_material		= cd_material_p or substr(verifica_material_similar(cd_material, cd_material_p),1,1) = 'S')
	and	((coalesce(cd_estab_regra::text, '') = '') or (cd_estab_regra = cd_estabelecimento_w))  LIMIT 1;
	exception
	when others then
		begin
		select	coalesce(ie_baixa_estoque,'S')
		into STRICT	ds_retorno_w
		from	componente_kit a
		where	cd_kit_material	= cd_kit_material_p
		and	((coalesce(cd_estab_regra::text, '') = '') or (cd_estab_regra = cd_estabelecimento_w))
		and	exists (	SELECT	1
				from	componente_kit_opcao x
				where	x.cd_kit_material = a.cd_kit_material
				and	x.nr_seq_componente = a.nr_sequencia
				and (x.cd_material = cd_material_p or substr(verifica_material_similar(x.cd_material, cd_material_p),1,1) = 'S'))  LIMIT 1;
		exception
		when others then
			ds_retorno_w	:=	'S';
		end;
	end;
end;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
