CREATE OR REPLACE FUNCTION obter_se_matmed_assoc_proc_lib (nr_prescricao_p bigint, nr_seq_proced_p bigint, ie_liberacao_p text) RETURNS varchar AS $body$
DECLARE


/*
D - Dose;
*/
ie_liberado_w	varchar(1);


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_proced_p IS NOT NULL AND nr_seq_proced_p::text <> '') and (ie_liberacao_p IS NOT NULL AND ie_liberacao_p::text <> '') then

	if (ie_liberacao_p = 'D') then

		select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_liberado_w
		from	prescr_material
		where	nr_prescricao	= nr_prescricao_p
		and	nr_sequencia_proc	= nr_seq_proced_p
		and	coalesce(qt_dose,0)	= 0;

	end if;

end if;

return ie_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
