CREATE OR REPLACE FUNCTION obter_se_mat_apresent_prescr (cd_material_p bigint, ie_tipo_material_p text, cd_convenio_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w varchar(1);
qt_regra_w bigint;
qt_regra_convenio_w bigint;

BEGIN

ds_retorno_w := 'S';

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

    -- check if has rules
    select  count(*)
    into STRICT    qt_regra_w
    from    regra_apresentacao_quimio
    where   ie_regra_aplicacao = 'P'
    and     ie_regra = 'MA'
    and     coalesce(ie_situacao, 'A') = 'A'
    and     cd_material = cd_material_p
    and     cd_estabelecimento = cd_estabelecimento_p;

    -- check if has rules with insurance
    select  count(*)
    into STRICT    qt_regra_convenio_w
    from    regra_apresentacao_quimio
    where   ie_regra_aplicacao = 'P'
    and     ie_regra = 'MA'
    and     coalesce(ie_situacao, 'A') = 'A'
    and     cd_convenio = cd_convenio_p
    and     cd_material = cd_material_p
    and     cd_estabelecimento = cd_estabelecimento_p;

    if (qt_regra_convenio_w > 0) then
    
      select  coalesce(max('S'), 'N')
      into STRICT    ds_retorno_w
      from    regra_apresentacao_quimio r
      where   r.ie_regra_aplicacao = 'P'
      and     coalesce(ie_situacao, 'A') = 'A'
      and     r.ie_regra = 'MA'
      and     r.cd_material = cd_material_p
      and (coalesce(r.dt_inicio_vigencia::text, '') = '' or clock_timestamp() >= inicio_dia(r.dt_inicio_vigencia))
      and (coalesce(r.dt_fim_vigencia::text, '') = '' or clock_timestamp() <= fim_dia(r.dt_fim_vigencia))
      and (coalesce(r.cd_estabelecimento::text, '') = '' or r.cd_estabelecimento = cd_estabelecimento_p)
      and (r.cd_convenio = cd_convenio_p)
      and (coalesce(r.ie_tipo_material_considerar::text, '') = '' or r.ie_tipo_material_considerar = ie_tipo_material_p);

    elsif (qt_regra_w > 0) then

      select  coalesce(max('S'), 'N')
      into STRICT    ds_retorno_w
      from    regra_apresentacao_quimio r
      where   r.ie_regra_aplicacao = 'P'
      and     coalesce(ie_situacao, 'A') = 'A'
      and     r.ie_regra = 'MA'
      and     r.cd_material = cd_material_p
      and (coalesce(r.dt_inicio_vigencia::text, '') = '' or clock_timestamp() >= inicio_dia(r.dt_inicio_vigencia))
      and (coalesce(r.dt_fim_vigencia::text, '') = '' or clock_timestamp() <= fim_dia(r.dt_fim_vigencia))
      and (coalesce(r.cd_estabelecimento::text, '') = '' or r.cd_estabelecimento = cd_estabelecimento_p)
      and (coalesce(r.cd_convenio::text, '') = '' or r.cd_convenio = cd_convenio_p)
      and (coalesce(r.ie_tipo_material_considerar::text, '') = '' or r.ie_tipo_material_considerar = ie_tipo_material_p);

    end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
