CREATE OR REPLACE FUNCTION obter_se_mat_autorizacao_sms (nr_seq_regra_p bigint, nr_seq_autor_p bigint) RETURNS varchar AS $body$
DECLARE


ie_mat_autor_w		varchar(15);
qt_regra_w		bigint;
cd_material_w		bigint;
cd_grupo_material_w	bigint;
cd_subgrupo_material_w	bigint;
cd_classe_material_w	bigint;

C01 CURSOR FOR
	SELECT	cd_material,
		cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material
	from	regra_envio_sms_material
	where	nr_seq_regra = nr_seq_regra_p
	order by cd_classe_material,
		cd_subgrupo_material,
		cd_grupo_material,
		cd_material;



BEGIN
select	count(*)
into STRICT	qt_regra_w
from	regra_envio_sms_material
where	nr_seq_regra = nr_seq_regra_p;

if (qt_regra_w > 0) then
	ie_mat_autor_w := 'N';

	open C01;
	loop
	fetch C01 into
		cd_material_w,
		cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_mat_autor_w
		from	material_autorizado a,
			autorizacao_convenio b,
			estrutura_material_v c
		where	a.nr_sequencia_autor = b.nr_sequencia
		and	b.nr_sequencia = nr_seq_autor_p
		and	c.cd_material = a.cd_material
		and	coalesce(cd_material_w,c.cd_material) = c.cd_material
		and	coalesce(cd_grupo_material_w,c.cd_grupo_material) = c.cd_grupo_material
		and	coalesce(cd_subgrupo_material_w,c.cd_subgrupo_material) = c.cd_subgrupo_material
		and	coalesce(cd_classe_material_w,c.cd_classe_material) = c.cd_classe_material;

		end;
	end loop;
	close C01;

else
	ie_mat_autor_w := 'S';
end if;

return	ie_mat_autor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
