CREATE OR REPLACE FUNCTION obter_se_mat_baixa_estoque_cir ( cd_material_p bigint, cd_local_estoque_p bigint, nr_cirurgia_p bigint, cd_kit_material_p bigint, nr_seq_kit_estoque_p bigint, nr_seq_interno_p bigint) RETURNS varchar AS $body$
DECLARE


ie_baixa_estoque_w	varchar(1);
cd_grupo_material_w	smallint;
cd_subgrupo_material_w	smallint;
cd_classe_material_w	integer;
cd_tipo_baixa_w		smallint := null;
cd_setor_atendimento_w	integer;
ie_possui_regra_w	varchar(1);
cd_setor_usuario_w	integer:= null;
nm_usuario_w		varchar(15);
ie_disp_pepo_w		varchar(1);
cd_material_kit_w	prescr_material.cd_material_kit%type;

C01 CURSOR FOR
	SELECT	cd_tipo_baixa
	from	material_estoque_pepo
	where	coalesce(cd_material,coalesce(cd_material_p,0))			= coalesce(cd_material_p,0)
	and	coalesce(cd_grupo_material,coalesce(cd_grupo_material_w,0))	= coalesce(cd_grupo_material_w,0)
	and	coalesce(cd_subgrupo_material,coalesce(cd_subgrupo_material_w,0))	= coalesce(cd_subgrupo_material_w,0)
	and	coalesce(cd_classe_material,coalesce(cd_classe_material_w,0))	= coalesce(cd_classe_material_w,0)
	and	coalesce(cd_local_estoque,coalesce(cd_local_estoque_p,0))		= coalesce(cd_local_estoque_p,0)
	and	coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_w,0))	= coalesce(cd_setor_atendimento_w,0)
	and	((coalesce(ie_somente_kit,'N') = 'N') or (coalesce(cd_kit_material_p,0) > 0) or (coalesce(nr_seq_kit_estoque_p,0) > 0) or (coalesce(cd_material_kit_w,0) > 0 and coalesce(ie_considerar_material_kit,'N') = 'S' ))
	and	coalesce(cd_setor_usuario,coalesce(cd_setor_usuario_w,0))		= coalesce(cd_setor_usuario_w,0)
	and	((coalesce(ie_disp_pepo,'N') = 'N') or (ie_disp_pepo_w = 'S'))
	order by coalesce(cd_grupo_material,99999999), coalesce(cd_subgrupo_material,99999999), coalesce(cd_classe_material,99999999), coalesce(cd_material,99999999);
						

BEGIN
cd_material_kit_w := 0;
if (coalesce(nr_seq_interno_p,0) > 0) then
	select	coalesce(max(nm_usuario_original),max(nm_usuario)),
		coalesce(max(ie_disp_pepo),'N')
	into STRICT	nm_usuario_w,
		ie_disp_pepo_w
	from	prescr_material
	where	nr_seq_interno = nr_seq_interno_p;
	if (nm_usuario_w IS NOT NULL AND nm_usuario_w::text <> '') then
		cd_setor_usuario_w := obter_setor_usuario(nm_usuario_w);
	end if;	
else
	cd_setor_usuario_w := wheb_usuario_pck.get_cd_setor_atendimento;
end if;	

select	coalesce(max('S'),'N')
into STRICT	ie_possui_regra_w
from	material_estoque_pepo;

if (ie_possui_regra_w = 'S') then
	begin

	cd_setor_atendimento_w	:= obter_unid_setor_cirurgia(nr_cirurgia_p,'C');

	select	max(cd_grupo_material),
		max(cd_subgrupo_material),
		max(cd_classe_material)
	into STRICT	cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w
	from	estrutura_material_v
	where	cd_material = cd_material_p;
	
	/*Na tabela PRESCR_MATERIAL grava o material que foi bipado como kit atraves coluna CD_MATERIAL_KIT, com isso, se tiver com este campo informado, identifica-se que foi usado material kit */

	if (coalesce(nr_seq_interno_p,0) > 0) then
		select	CD_MATERIAL_KIT
		into STRICT	cd_material_kit_w
		from	prescr_material
		where	nr_seq_interno = nr_seq_interno_p;
	end if;	

	
	open C01;
	loop
	fetch C01 into	
		cd_tipo_baixa_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		
		cd_tipo_baixa_w	:= cd_tipo_baixa_w;
		
		end;
	end loop;
	close C01;

	end;
end if;

/*
if	(cd_material_p > 0) then
	select	nvl(max('N'),'S')
	into	ie_baixa_estoque_w
	from	material_estoque_pepo
	where	cd_material					= cd_material_p
	and	nvl(cd_local_estoque,nvl(cd_local_estoque_p,0))	= nvl(cd_local_estoque_p,0);
end if;
*/
return	cd_tipo_baixa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_mat_baixa_estoque_cir ( cd_material_p bigint, cd_local_estoque_p bigint, nr_cirurgia_p bigint, cd_kit_material_p bigint, nr_seq_kit_estoque_p bigint, nr_seq_interno_p bigint) FROM PUBLIC;

