SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_mat_bras_simp_vinc ( cd_medicamento_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao:		B - Brasindice	S - Simpro	*/

ds_retorno_w 		varchar(1) := 'N';
qt_registros_w		integer	:= 0;


BEGIN

if (ie_opcao_p = 'B') then
	select	count(*)
	into STRICT	qt_registros_w
	from	material_brasindice
	where	cd_medicamento = cd_medicamento_p
	and	ie_situacao = 'A';
end if;

if (ie_opcao_p = 'S') then
	select	count(*)
	into STRICT	qt_registros_w
	from	material_simpro
	where	cd_simpro = somente_numero(cd_medicamento_p);
end if;

if (qt_registros_w > 0) then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
