CREATE OR REPLACE FUNCTION obter_se_mat_ciclo_autorizado ( nr_seq_atendimento_p paciente_atendimento.nr_seq_atendimento%type) RETURNS varchar AS $body$
DECLARE


nr_seq_paciente_w		paciente_atendimento.nr_seq_paciente%type;
cd_material_w			paciente_atend_medic.cd_material%type;
qt_autorizado_w			bigint;
qt_reg_autorizacao_w	bigint;
ie_retorno_w			varchar(1) := 'S';

c01 CURSOR FOR
SELECT	cd_material
from	paciente_atend_medic
where	nr_seq_atendimento = nr_seq_atendimento_p;


BEGIN

select	max(nr_seq_paciente)
into STRICT	nr_seq_paciente_w
from	paciente_atendimento
where	nr_seq_atendimento = nr_seq_atendimento_p;

open c01;
loop
fetch c01 into cd_material_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	count(a.nr_sequencia)
	into STRICT	qt_reg_autorizacao_w
	from	material_autorizado b,
			autorizacao_convenio a
	where	a.nr_sequencia		= b.nr_sequencia_autor
	and		a.nr_seq_paciente_setor	= nr_seq_paciente_w
	and		b.cd_material			= cd_material_w;

	if (qt_reg_autorizacao_w > 0) then
		select	count(a.nr_sequencia)
		into STRICT	qt_autorizado_w
		from	material_autorizado b,
				autorizacao_convenio a
		where	a.nr_sequencia		    = b.nr_sequencia_autor
		and		b.qt_autorizada			> 0
		and		a.nr_seq_paciente_setor	= nr_seq_paciente_w
		and		b.cd_material			= cd_material_w;

		if (qt_autorizado_w = 0) then
			ie_retorno_w := 'N';
			exit;
		end if;

	end if;
	end;
end loop;
close c01;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
