CREATE OR REPLACE FUNCTION obter_se_mat_controla_serie ( cd_estabelecimento_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_controla_serie_w				varchar(1);

/* Legenda Retorno
N - Não controla
P - Permite informar
O - Obriga informação
*/
BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

	select	coalesce(max(ie_controla_serie),'N')
	into STRICT	ie_controla_serie_w
	from	material_estab
	where	cd_estabelecimento = cd_estabelecimento_p
	and	cd_material = cd_material_p;

end if;

return ie_controla_serie_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
