SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_mat_conv_autorizado ( cd_mat_autorizado_p bigint, cd_material_p bigint, nr_seq_regra_quimio_p bigint) RETURNS varchar AS $body$
DECLARE


retorno_w			varchar(1) := 'N';
ie_regra_w			varchar(15);
cd_material_especifico_w	integer;
cd_material_w			integer;
nr_seq_ficha_tecnica_w		bigint;
nr_seq_ficha_tec_autor_w	bigint;
cd_medicamento_w		integer;


BEGIN

if (coalesce(nr_seq_regra_quimio_p,0) > 0) then


	select	max(ie_regra),
		max(cd_material_especifico),
		max(cd_material)
	into STRICT	ie_regra_w,
		cd_material_especifico_w,
		cd_material_w
	from	regra_apresentacao_quimio
	where	nr_sequencia = nr_seq_regra_quimio_p;

	if (ie_regra_w IS NOT NULL AND ie_regra_w::text <> '') then

		If (ie_regra_w = 'ME') then

			If (cd_mat_autorizado_p = cd_material_especifico_w) and (cd_material_w = cd_material_p) then

				retorno_w :=  'S';

			else

				retorno_w :=  'N';


			end if;

		elsif (ie_regra_w = 'MF')  then

			select 	coalesce(max(nr_seq_ficha_tecnica),0)
			into STRICT 	nr_seq_ficha_tecnica_w
			from 	material
			where 	cd_material = cd_material_p;


			select 	coalesce(max(cd_material),0)
			into STRICT 	cd_medicamento_w
			from 	material
			where 	ie_tipo_material = 8
			and 	nr_seq_ficha_tecnica = nr_seq_ficha_tecnica_w;

			if (cd_medicamento_w > 0) and (cd_mat_autorizado_p = cd_medicamento_w) then

				retorno_w :=  'S';

			else

				retorno_w :=  'N';

			end if;


		elsif (ie_regra_w = 'MA')  then

			select 	coalesce(max(nr_seq_ficha_tecnica),0)
			into STRICT 	nr_seq_ficha_tecnica_w
			from 	material
			where 	cd_material = cd_material_p;


			select 	coalesce(max(nr_seq_ficha_tecnica),0)
			into STRICT 	nr_seq_ficha_tec_autor_w
			from 	material
			where 	cd_material = cd_mat_autorizado_p;


			if (nr_seq_ficha_tecnica_w = nr_seq_ficha_tec_autor_w) then

				retorno_w :=  'S';

			else

				retorno_w :=  'N';

			end if;


		end if;


	end if;


end if;

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
