CREATE OR REPLACE FUNCTION obter_se_mat_conv_ml ( cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_conv_ml_w		varchar(1);
cd_unid_med_consumo_w	material.cd_unidade_medida_consumo%type;


BEGIN

select	cd_unidade_medida_consumo
into STRICT	cd_unid_med_consumo_w
from	material
where	cd_material = cd_material_p;

if (upper(cd_unid_med_consumo_w) <> upper(obter_unid_med_usua('ml'))) then
	select	coalesce(max('S'),'N')
	into STRICT	ie_conv_ml_w
	from	material_conversao_unidade
	where	cd_material = cd_material_p
	and	cd_unidade_medida = obter_unid_med_usua('ml');
else
	ie_conv_ml_w := 'S';
end if;

return	ie_conv_ml_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
