CREATE OR REPLACE FUNCTION obter_se_mat_estrutura ( cd_grupo_material_p bigint, cd_subgrupo_material_p bigint, cd_classe_material_p bigint, nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_pertence_w 	varchar(10);


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_pertence_w
from	estrutura_material_v b,
	material a,
	prescr_material p
where	a.cd_material = b.cd_material
and	a.cd_material = p.cd_material
and	p.nr_prescricao = nr_prescricao_p
and	b.cd_grupo_material = coalesce(cd_grupo_material_p,b.cd_grupo_material)
and	b.cd_subgrupo_material = coalesce(cd_subgrupo_material_p,b.cd_subgrupo_material)
and	b.cd_classe_material = coalesce(cd_classe_material_p,b.cd_classe_material);


return	ie_pertence_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
