CREATE OR REPLACE FUNCTION obter_se_mat_exibe_desc_cir ( cd_material_p bigint, nr_cirurgia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(1) := 'N';
ie_exige_justificativa_w	varchar(1) := 'N';
cd_grupo_material_w		smallint;
cd_subgrupo_material_w		smallint;
cd_classe_material_w		integer;
nr_seq_familia_w		bigint;
cd_convenio_w			bigint;



BEGIN




if (coalesce(nr_cirurgia_p,0) > 0) then
	select max(cd_convenio)
	into STRICT   cd_convenio_w
	from   cirurgia
	where  nr_cirurgia = nr_cirurgia_p;
end if;

if (coalesce(cd_material_p,0) > 0) then

	select	max(cd_classe_material),
		max(cd_subgrupo_material),
		max(cd_grupo_material),
		max(nr_seq_familia)
	into STRICT	cd_classe_material_w,
		cd_subgrupo_material_w,
		cd_grupo_material_w,
		nr_seq_familia_w
	from	estrutura_material_v
	where	cd_material = cd_material_p;
end if;

if (coalesce(cd_material_p,0) > 0) then

	select	coalesce(max('S'),'N'),
		coalesce(max(ie_exige_justificativa),'N')
	into STRICT	ie_retorno_w,
		ie_exige_justificativa_w
	from	regra_material_desc_cirur
	where	coalesce(cd_material, cd_material_p) = cd_material_p
	and	coalesce(cd_classe_material, coalesce(cd_classe_material_w,0)) = coalesce(cd_classe_material_w,0)
	and	coalesce(cd_subgrupo_material, coalesce(cd_subgrupo_material_w,0)) = coalesce(cd_subgrupo_material_w,0)
	and	coalesce(cd_grupo_material, coalesce(cd_grupo_material_w,0)) = coalesce(cd_grupo_material_w,0)
	and	coalesce(cd_convenio, coalesce(cd_convenio_w,0)) = coalesce(cd_convenio_w,0);

end if;

if (ie_opcao_p = 'J') then
	ie_retorno_w := ie_exige_justificativa_w;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
