CREATE OR REPLACE FUNCTION obter_se_mat_existe_padrao_loc ( cd_material_p bigint, cd_local_estoque_p bigint) RETURNS varchar AS $body$
DECLARE


qt_existe_w	bigint;
ie_existe_w	varchar(1);


BEGIN

ie_existe_w	:= 'N';

select	count(*)
into STRICT	qt_existe_w
from	padrao_estoque_local
where	cd_material		= cd_material_p
and	cd_local_estoque	= cd_local_estoque_p;

if (qt_existe_w > 0) then
	ie_existe_w := 'S';
end if;

return	ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
