CREATE OR REPLACE FUNCTION obter_se_mat_generico (cd_material_base_p bigint, cd_material_consulta_p bigint) RETURNS varchar AS $body$
DECLARE


ie_generico_w		varchar(01)	:= 'N';


BEGIN

if (cd_material_base_p	= cd_material_consulta_p) then
	ie_generico_w		:= 'S';
else
	select	coalesce(max('S'),'N')
	into STRICT	ie_generico_w
	from	material
	where	cd_material					= cd_material_base_p
	and	coalesce(cd_material_generico, cd_material)		= cd_material_consulta_p;
end if;

return	ie_generico_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
