CREATE OR REPLACE FUNCTION obter_se_mat_gera_lote_sep ( nr_seq_lote_p bigint) RETURNS varchar AS $body$
DECLARE


qt_existe_lote_w	bigint;
ie_retorno_w 		varchar(1):= 'N';



BEGIN

select 	count(*)
into STRICT	qt_existe_lote_w
from	ap_lote_item a,
	material d
where	a.cd_material = d.cd_material
and	nr_seq_lote = nr_seq_lote_p
and	coalesce(d.ie_gera_lote_separado, 'N') = 'S';

if (coalesce(qt_existe_lote_w,0) > 0)	then
	ie_retorno_w := 'S';
end if;


return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
