CREATE OR REPLACE FUNCTION obter_se_mat_kit ( cd_estabelecimento_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_mat_kit_w		varchar(1)	:= 'N';
cd_kit_material_w		integer;


BEGIN

ie_mat_kit_w	:= 'N';

select	coalesce(max(cd_kit_material),0)
into STRICT	cd_kit_material_w
from	material_estab
where	cd_material	= cd_material_p
and	cd_estabelecimento	= cd_estabelecimento_p;
if (cd_kit_material_w > 0) then
	ie_mat_kit_w	:= 'S';
end if;

RETURN ie_mat_kit_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
