CREATE OR REPLACE FUNCTION obter_se_mat_leite_mae (cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_leite_materno_w	varchar(1);

BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_leite_materno_w
from	classif_leite_deriv c,
	nutricao_leite_deriv b
where	b.nr_seq_classif	= c.nr_sequencia
and	b.cd_material		= cd_material_p
and coalesce(b.ie_situacao,'A') = 'A'
and	c.ie_leite_materno	= 'S';

return	ie_leite_materno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
