SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_mat_lib_fornec ( cd_cnpj_p text, cd_material_p bigint, cd_estabelecimento_p bigint, ie_funcao_consiste_p text) RETURNS varchar AS $body$
DECLARE


ie_qualificado_w			varchar(15);
cd_grupo_material_w			smallint;
cd_subgrupo_material_w			smallint;
cd_classe_material_w			integer;
nr_sequencia_w				bigint;
ie_fornec_homologado_w			varchar(15);
qt_existe_w				bigint;

/*
ie_funcao_consiste_p - Função que será realizada a consistêmcia
C   - Cotação de Compra;
O   - Ordem Compra;
NF - Nota Fiscal;
*/
c01 CURSOR FOR
SELECT	nr_sequencia
from	mat_lib_estrut_fornec
where	cd_cnpj_fornec = cd_cnpj_p
and	((coalesce(cd_estabelecimento::text, '') = '') or
	(cd_estabelecimento IS NOT NULL AND cd_estabelecimento::text <> '' AND cd_estabelecimento = cd_estabelecimento_p))
and	coalesce(cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
and	coalesce(cd_material, cd_material_p)			= cd_material_p
and     (((coalesce(ie_cotacao,'N') = 'S') and (ie_funcao_consiste_p = 'C')) or
	((coalesce(ie_ordem,'N')   = 'S') and (ie_funcao_consiste_p = 'O')) or
        ((coalesce(ie_nota,'N')    = 'S') and (ie_funcao_consiste_p = 'NF')))
order by
	coalesce(cd_material, 0),
	coalesce(cd_classe_material, 0),
	coalesce(cd_subgrupo_material, 0),
	coalesce(cd_grupo_material, 0);


BEGIN

begin
	select	1
	into STRICT	qt_existe_w
	from	mat_lib_estrut_fornec
	where	cd_cnpj_fornec = cd_cnpj_p  LIMIT 1;
exception
when others then
	qt_existe_w := 0;
end;

/*Recebe padrão = S*/

ie_qualificado_w := 'S';

if (qt_existe_w > 0) then

	select	coalesce(ie_fornec_homologado,'S')
	into STRICT	ie_fornec_homologado_w
	from	parametro_compras
	where	cd_estabelecimento = cd_estabelecimento_p;


	/*Mas se esse parâmetro de compras está N, o padrão do campo será N também*/

	if (ie_fornec_homologado_w = 'N') then
		ie_qualificado_w := 'N';
	end if;

	select	cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material
	into STRICT	cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w
	from	estrutura_material_v
	where 	cd_material	= cd_material_p;


	open C01;
	loop
	fetch C01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		select	coalesce(ie_qualificado, 'S')
		into STRICT	ie_qualificado_w
		from	mat_lib_estrut_fornec
		where	nr_sequencia = nr_sequencia_w;

		end;
	end loop;
	close C01;
end if;


return	ie_qualificado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
