CREATE OR REPLACE FUNCTION obter_se_mat_lib_grupo_compras ( nr_seq_grupo_compras_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


cd_grupo_material_w			smallint;
cd_subgrupo_material_w			smallint;
cd_classe_material_w			integer;
ie_liberado_w				varchar(1) := 'N';


c01 CURSOR FOR
SELECT	coalesce(a.ie_liberado, 'N')
from	sup_estrut_compras a,
	sup_subgrupo_compra b,
	sup_grupo_compra c
where	a.nr_seq_subgrupo_compra = b.nr_sequencia
and	b.nr_seq_grupo_compra = c.nr_sequencia
and	c.nr_sequencia = nr_seq_grupo_compras_p
and	coalesce(a.cd_grupo_material,cd_grupo_material_w) 		= cd_grupo_material_w
and	coalesce(a.cd_subgrupo_material,cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(a.cd_classe_material,cd_classe_material_w) 		= cd_classe_material_w
and	coalesce(a.cd_material,cd_material_p) 			= cd_material_p
and	coalesce(c.ie_situacao,'A') = 'A'
and	coalesce(b.ie_situacao,'A') = 'A'
order by	coalesce(cd_material,0),
		coalesce(cd_classe_material,0),
		coalesce(cd_subgrupo_material,0),
		coalesce(cd_grupo_material,0);


BEGIN

select	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_material_v
where	cd_material = cd_material_p;

open C01;
loop
fetch C01 into
	ie_liberado_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_liberado_w := ie_liberado_w;
	end;
end loop;
close C01;

return ie_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
