SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_mat_lib_operacao_nf ( cd_operacao_nf_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'S';
qt_existe_w		bigint;
cd_grupo_material_w	integer;
cd_subgrupo_material_w	integer;
cd_classe_material_w	integer;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	operacao_estrutura_lib
where	cd_operacao_nf = cd_operacao_nf_p;

if (qt_existe_w > 0) then
	begin

	select	cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material
	into STRICT	cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w
	from	estrutura_material_v
	where	cd_material = cd_material_p;

	select	coalesce(max(ie_permite),'S')
	into STRICT	ds_retorno_w
	from	operacao_estrutura_lib
	where	cd_operacao_nf					= cd_operacao_nf_p
	and	coalesce(cd_material,cd_material_p) 			= cd_material_p
	and 	coalesce(cd_classe_material,cd_classe_material_w)		= cd_classe_material_w
	and 	coalesce(cd_subgrupo_material,cd_subgrupo_material_w)	= cd_subgrupo_material_w
	and 	coalesce(cd_grupo_material,cd_grupo_material_w)		= cd_grupo_material_w;

	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w	:= 'S';
	end if;

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
