CREATE OR REPLACE FUNCTION obter_se_mat_lista_compras ( nr_seq_lista_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_pertence_lista_w			varchar(1) := 'N';
qt_registro_w			bigint;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	sup_lista_compras_itens
where	nr_seq_lista = nr_seq_lista_p
and	cd_material = cd_material_p;

if (qt_registro_w > 0) then
	ie_pertence_lista_w := 'S';
end if;

return	ie_pertence_lista_w;

end;
$body$
LANGUAGE PLPGSQL
