CREATE OR REPLACE FUNCTION obter_se_mat_localizacao ( cd_local_estoque_p bigint, cd_material_p bigint, ds_localizacao_p text, nr_seq_local_p text) RETURNS varchar AS $body$
DECLARE


qt_existe_w		bigint;
ie_existe_w		varchar(1) := 'N';


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	localizacao_estoque_local
where	cd_local_estoque = cd_local_estoque_p
and	cd_material = cd_material_p
and	((coalesce(nr_seq_local_p::text, '') = '' and obter_se_contido_char(elimina_caractere_especial(upper(ds_localizacao)),upper(ds_localizacao_p)) = 'S')
or ((nr_seq_local_p IS NOT NULL AND nr_seq_local_p::text <> '') and obter_se_contido_char(elimina_caractere_especial(upper(nr_seq_local)),upper(nr_seq_local_p)) = 'S'));

if (qt_existe_w = 0) then
	select	count(*)
	into STRICT	qt_existe_w
	from	padrao_estoque_local
	where	cd_local_estoque = cd_local_estoque_p
	and	cd_material = cd_material_p
	and	obter_se_contido_char(elimina_caractere_especial(upper(ds_localizacao)),upper(ds_localizacao_p)) = 'S';
end if;

if (qt_existe_w > 0) then
	ie_existe_w := 'S';
end if;

return	ie_existe_w;

end	;
$body$
