CREATE OR REPLACE FUNCTION obter_se_mat_lote_atendido ( nr_atendimento_p bigint, cd_material_p bigint, nr_seq_lote_p bigint, qt_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1):='N';
qt_material_w	material_atend_paciente.qt_material%type;


BEGIN

select	coalesce(sum(qt_estoque),sum(qt_material),0)
into STRICT	qt_material_w
from	material_atend_paciente
where	nr_seq_lote_fornec = nr_seq_lote_p
and	nr_atendimento 	   = nr_atendimento_p;

if (qt_material_w >= qt_material_p) then
	ds_retorno_w:='S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
