CREATE OR REPLACE FUNCTION obter_se_mat_lote_validade (nr_atendimento_p bigint, cd_material_p bigint, dt_material_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_material_validade_w		varchar(01)	:= 'N';
qt_reg_w			bigint;
dt_validade_w			timestamp;

C01 CURSOR FOR
	SELECT	b.dt_validade
	from	material_lote_fornec b,
		material_atend_paciente a
	where	a.NR_SEQ_LOTE_FORNEC	= b.nr_sequencia
	and	a.nr_atendimento	= nr_atendimento_p
	and	coalesce(a.cd_motivo_exc_conta::text, '') = ''
	and	a.cd_material		= cd_material_p
	order by b.dt_validade;


BEGIN

open C01;
loop
fetch C01 into
	dt_validade_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (dt_validade_w >= dt_material_p) then
		ie_material_validade_w	:= 'S';
	end if;

	end;
end loop;
close C01;

return	ie_material_validade_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
