CREATE OR REPLACE FUNCTION obter_se_mat_possui_movto ( cd_material_p bigint, cd_estabelecimento_p bigint, qt_dias_p bigint) RETURNS varchar AS $body$
DECLARE


/*Se informar QT_DIAS_P = 0, desconsidera o parÃ¢metro
   Se informar QT_DIAS_P > 0, considera*/
cd_material_estoque_w			integer;
qt_existe_w				bigint;
ie_existe_w				varchar(1) := 'N';


BEGIN

select	cd_material_estoque
into STRICT	cd_material_estoque_w
from	material
where	cd_material = cd_material_p;

if (qt_dias_p = 0) then
	select	count(*)
	into STRICT	qt_existe_w
	from	movimento_estoque
	where	cd_estabelecimento = cd_estabelecimento_p
	and	cd_material_estoque = cd_material_estoque_w;
else
	select	count(*)
	into STRICT	qt_existe_w
	from	movimento_estoque
	where	cd_estabelecimento = cd_estabelecimento_p
	and	cd_material_estoque = cd_material_estoque_w
	and	dt_movimento_Estoque >= clock_timestamp() - qt_dias_p;
end if;

if (qt_existe_w > 0) then
	ie_existe_w := 'S';
end if;

return	ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
